import React from "react";
import styled, { css } from "styled-components";

const Button = (props) => {
  // eslint-disable-next-line default-case
  switch (props.name) {
    case "primary":
      return <But primary>{props.children}</But>;

    case "bad":
      return <But>{props.children}</But>;
  }
};

export default Button;

const But = styled.button`
  background: transparent;
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0.5em 1em;
  padding: 0.25em 1em;
  &:hover {
    background: palevioletred;
    color: white;
  }

  ${(props) =>
    props.primary &&
    css`
      background: palevioletred;
      color: white;
      &:hover {
        background: white;
        color: palevioletred;
      }
    `}
`;
